import React from 'react';
import axios from 'axios';
import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom'
import Find from './Find';
function Delete() {
    const App = () => {
        const [myData, setMyData] = useState([]);
        const [isError, setIsError] = useState("");
        const[myData1,setMyData1]=useState([]);
        // using Promises
        useEffect(() => {
            axios
                .get("http://localhost:8989/getAllStudent")
                .then((response) => {
                    console.log(response.data)
                    setMyData(response.data)
                })
                .catch((error) => setIsError(error.message));
        }, []);

        //plz subscribe to thapa technical
        return (
            <>
                {isError !== "" && <h2>{isError}</h2>}

                <div className="grid">
                    <h1>All users</h1>
                    <table className="table">
                        <thead>
                            <tr>
                                <th>S.NO</th>
                                <th>Name</th>
                                <th>RollNumber</th>
                                <th>Age</th>
                                <th>Department</th>
                                <th>Address</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                myData.map((data, index) => {
                                    return (
                                        <tr key={data.roll}>
                                            <td>{index + 1}</td>
                                            <td>{data.name}</td>
                                            <td>{data.roll}</td>
                                            <td>{data.age}</td>
                                            <td>{data.department}</td>
                                            <td>{data.address}</td>

                                        </tr>
                                    )
                                })
                            }
                        </tbody>
                    </table>
                </div>
            </>
        );
    };
    const deleteByValue = value => {
        setMyData1(oldValues => {
            return oldValues.filter(myData1 => myData1.roll !== value)
        })
    }
    const [roll, setRoll] = useState();
    const [error, setError] = useState('');
    function del() {
        // useEffect(() => {
            deleteByValue(roll);
        axios
            .delete('http://localhost:8989/deleteStudent?roll=' + roll)
            .then((response) => {
                console.log(response.data);
            })
            .catch((error) => setError(error.message));
        alert("User Deleted");
    }
    //  []);
    // }
    return (
        <>
            <h2>Delete User</h2>
            <div>
                <input type="number" placeholder='Enter Roll Number' className="form-control1" value={roll} onChange={(e) => setRoll(e.target.value)}>
                </input>
            </div>

            <button type="submit" class="btn btn-primary" onClick
                ={del}>Submit</button>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
            <Link to='/'>
                <button type="submit" class="btn btn-primary"
                >Back</button>
            </Link>
            <h2>
                {error}
            </h2>
            <div className='Find'>

                <Find />
            </div>
        </>
    );
}

export default Delete;
