import * as React from 'react';
import CssBaseline from '@mui/material/CssBaseline';
import Box from '@mui/material/Box';
import Container from '@mui/material/Container';
import { useState ,useEffect} from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Link} from 'react-router-dom';
import '../style/Main.css'
export default function SimpleContainer() {
    const[id,setId]=useState('');
    const[name,setName]=useState("");
    const[roll,setRoll]=useState();
    const[address,setAddress]=useState("");
    async function save(event) {
        event.preventDefault();
        try {
            await axios.post("http://localhost:8989/saveStudent",
                {

                    name: name,
                    address: address,
                    roll:roll

                });
            alert("Student Registation Successfully");
            setName("");
            setAddress("");
            setRoll();


        }
        catch (err) {
            alert("User Registation Failed");
        }
    }

    return (
        <div className="container mt-4">
            <form>
                <div class="form-group">
                    <label>Name</label>
                    <input type="text" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter Name" 
                    value={name} onChange={(event)=>{
                        setName(event.target.value);
                    }}/>

                </div>
                <div class="form-group">
                    <label>RollNumber</label>
                    <input type="number" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter Roll Number"
                        value={roll} onChange={(event) => {
                            setRoll(event.target.value);
                        }} />
                </div>
                <div class="form-group form-check">
                    <label>Address</label>
                    <input type="text" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter Address"
                        value={address} onChange={(event) => {
                            setAddress(event.target.value);
                        }} />
                </div>
                <div className='login'>
                    <div className=''>

                <button type="submit" class="btn btn-primary" onClick
                ={save}>Submit</button>
                    </div>

                <Link to='/Main'>
                <button type="submit" class="btn btn-primary" 
                    >Back</button></Link>
                    </div>
            </form>
        </div>
    );
}
