import { useState, useEffect } from "react";
import "../style/Main.css";
import axios from "axios";
import { Link } from 'react-router-dom'
const App = () => {
    const [id, setId] = useState();
    const [data, setData] = useState([]);
    const [error, setError] = useState('');
    // using Promises
    function fin() {
        // useEffect(() => {
        axios
            .get("http://localhost:8989/getStudent?id=" + id)
            .then((response) => {
                setData(response.data)
                // if(response.data===null){
                //     console.log("error")
                // }
            });
        // console.log(data)
        // if(data===null){
        //         // setError("NotFound");
        //         console.log("Error");
        //     }
        // .catch((error) => setIsError(error.message));
    }
    // []);}

    //plz subscribe to thapa technical
    return (
        <>
            <h1>users</h1>
            {/* {isError !== "" && <h2>{isError}</h2>} */}
            <div>

                <input type="number" placeholder='Enter ID Number' className="form-control1" value={id} onChange={(e) => setId(e.target.value)}>

                </input>
            </div>
            <button type="submit" class="btn btn-primary" onClick
                ={fin}>Submit</button>  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
            <Link to='/Main'>
                <button type="submit" class="btn btn-primary"
                >Back</button></Link>
        <div className="body">
            <h2>
                {data.id}
            </h2>
            <h3>
                {data.name}
            </h3>
            <h3>
                {data.address}
            </h3>
            <h3>
                {error}
            </h3>
            </div>
            <div className='div'>
                <Link to='/Find'> <button type="submit" class="btn btn-primary"
                >FindAll</button></Link>
            </div>
        </>
    );
};

export default App;
